import numpy as np
import pandas as pd
import pickle
from collections import Counter
from sklearn import svm, cross_validation, neighbors
from sklearn.ensemble import VotingClassifier, RandomForestClassifier

def processDataForLabels(ticker):
    """Processes data in order to label the stocks, buy/sell/hold
    
    Processes the daily data from the CSV and modifies the dataframes
    in order to track the price and make a determination based upon that price
    
    Args:
        Recieves ticker from a predetermined CSV file
        
    Returns:
        Returns tickers(a dataframe of values) and df(our dataframe contianing the 
        moving price average)
        
    Raises:
        N/A
    
    """
    hmDays = 7
    df = pd.read_csv("SP500JoinedToCSV.csv", index_col = 0)
    tickers = df.columns.values.tolist()
    df.fillna(0, inplace = True)
    
    # Goes through and for every 7 days plots a negative shift
    for i in range(1, hmDays + 1):
        df['{}_{}d'.format(ticker, i)] = (df[ticker].shift(-i) - df[ticker]) / df[ticker] 
        
    df.fillna(0, inplace = True) 
    return tickers, df


def buySellHold(*args):
    cols = [c for c in args]
    req = 0.02
    
    for col in cols:
        if col > req:
            return 1
        if col > -req:
            return -1
    return 0


def extractFeaturesets(ticker):
    tickers, df =processDataForLabels(ticker)
    
    df["{}_target".format(ticker)] = list(map(buySellHold, 
      df['{}_1d'.format(ticker)],
      df['{}_2d'.format(ticker)],
      df['{}_3d'.format(ticker)],
      df['{}_4d'.format(ticker)],
      df['{}_5d'.format(ticker)],
      df['{}_6d'.format(ticker)],
      df['{}_7d'.format(ticker)]
      ))
    
    vals = df["{}_target".format(ticker)].values.tolist()
    strVals = [str(i) for i in vals]
    print(ticker + "\n" + "---" * 25)
    print("Data spread: ", Counter(strVals))
    df.fillna(0, inplace = True)
    
    df = df.replace([np.inf, -np.inf], np.nan)
    df.dropna(inplace = True)
    
    dfVals = df[[ticker for ticker in tickers]].pct_change()
    dfVals = dfVals.replace([np.inf, -np.inf], 0)
    dfVals.fillna(0, inplace = True)
    
    X = dfVals.values
    y = df["{}_target".format(ticker)].values
           
    return X, y, df
    

def doML(ticker):
    X, y, df = extractFeaturesets(ticker)
    
    XTrain, XTest, yTrain, yTest = cross_validation.train_test_split(X, y, test_size = 0.25)
    
    clf = neighbors.KNeighborsClassifier()
    clf = VotingClassifier([("lsvc", svm.LinearSVC()),
                            ("Knn", neighbors.KNeighborsClassifier()),
                            ("Rfor", RandomForestClassifier())])
    
    clf.fit(XTrain, yTrain)
    confidence = clf.score(XTest, yTest)
    print("Accuracy: ", confidence)
    
    predictions = clf.predict(XTest)
    
    print("Predicted spread: ", Counter(predictions))
    
    return confidence


